// Validar y estructurar datos del cliente
const clienteData = $input.first().json;

// Validaciones b치sicas
if (!clienteData.ingresoNeto || !clienteData.tipoProducto) {
  throw new Error('Faltan datos obligatorios: ingresoNeto, tipoProducto');
}

// Estructura normalizada del cliente
const cliente = {
  // Datos b치sicos
  ingresoNeto: clienteData.ingresoNeto || 0,
  tipoProducto: clienteData.tipoProducto || 'auto', // auto, inmueble, seguro
  montoSolicitado: clienteData.montoSolicitado || 0,

  // Perfil crediticio
  scoring: clienteData.scoring || 600,
  antiguedadLaboral: clienteData.antiguedadLaboral || 12,
  deudaActual: clienteData.deudaActual || 0,

  // Preferencias
  tasaMaxima: clienteData.tasaMaxima || 50,
  plazoMaximo: clienteData.plazoMaximo || 60,
  cuotaMaxima: clienteData.cuotaMaxima || clienteData.ingresoNeto * 0.35,

  // Metadata
  vendedor: clienteData.vendedor || 'sistema',
  timestamp: new Date().toISOString()
};

return { cliente };

// Simular obtenci칩n de planes desde m칰ltiples fuentes
// En producci칩n, estos ser칤an HTTP Requests a APIs reales

const cliente = $input.first().json.cliente;
const tipoProducto = cliente.tipoProducto;

// Base de datos mock de planes
const planesDisponibles = {
  auto: [
    {
      id: 'banco_a_auto_1',
      entidad: 'Banco A',
      producto: 'Cr칠dito Automotor Cl치sico',
      tasa: 42.5,
      plazoMinimo: 12,
      plazoMaximo: 84,
      montoMinimo: 500000,
      montoMaximo: 8000000,
      requisitos: {
        scoringMinimo: 550,
        antiguedadMinima: 6,
        ingresoMinimo: 80000
      },
      beneficios: ['Seguro incluido', 'Sin gastos administrativos'],
      comisiones: {
        apertura: 1.5,
        mantenimiento: 0
      }
    },
    {
      id: 'banco_b_auto_1',
      entidad: 'Banco B',
      producto: 'Auto F치cil',
      tasa: 38.9,
      plazoMinimo: 12,
      plazoMaximo: 72,
      montoMinimo: 300000,
      montoMaximo: 6000000,
      requisitos: {
        scoringMinimo: 600,
        antiguedadMinima: 12,
        ingresoMinimo: 100000
      },
      beneficios: ['Tasa fija', 'Pre-aprobaci칩n r치pida'],
      comisiones: {
        apertura: 2.0,
        mantenimiento: 150
      }
    },
    {
      id: 'financiera_a_auto_1',
      entidad: 'Financiera A',
      producto: 'Cr칠dito Automotor Express',
      tasa: 48.0,
      plazoMinimo: 6,
      plazoMaximo: 60,
      montoMinimo: 200000,
      montoMaximo: 5000000,
      requisitos: {
        scoringMinimo: 500,
        antiguedadMinima: 3,
        ingresoMinimo: 60000
      },
      beneficios: ['Aprobaci칩n inmediata', 'Menos requisitos'],
      comisiones: {
        apertura: 3.0,
        mantenimiento: 200
      }
    }
  ],
  inmueble: [
    {
      id: 'banco_a_hipoteca_1',
      entidad: 'Banco A',
      producto: 'Cr칠dito Hipotecario Tradicional',
      tasa: 35.0,
      plazoMinimo: 60,
      plazoMaximo: 360,
      montoMinimo: 2000000,
      montoMaximo: 50000000,
      requisitos: {
        scoringMinimo: 650,
        antiguedadMinima: 24,
        ingresoMinimo: 200000
      },
      beneficios: ['Tasa preferencial', 'Seguro de vida incluido'],
      comisiones: {
        apertura: 1.0,
        mantenimiento: 0
      }
    },
    {
      id: 'banco_c_hipoteca_1',
      entidad: 'Banco C',
      producto: 'Mi Primera Casa',
      tasa: 32.5,
      plazoMinimo: 120,
      plazoMaximo: 300,
      montoMinimo: 1500000,
      montoMaximo: 30000000,
      requisitos: {
        scoringMinimo: 700,
        antiguedadMinima: 36,
        ingresoMinimo: 250000
      },
      beneficios: ['Tasa promocional', 'Sin gastos de tasaci칩n'],
      comisiones: {
        apertura: 0.5,
        mantenimiento: 100
      }
    }
  ],
  seguro: [
    {
      id: 'seguradora_a_auto_1',
      entidad: 'Seguradora A',
      producto: 'Seguro Auto Completo',
      tasa: 0, // Los seguros no tienen tasa, pero usamos para premio anual
      premio_anual: 45000,
      cobertura: 'Todo Riesgo',
      franquicia: 15000,
      requisitos: {
        antiguedadVehiculo: 10,
        valorMinimo: 500000
      },
      beneficios: ['Auxilio mec치nico', 'Auto sustituto', 'Cristales bonificados'],
      descuentos: {
        sinSiniestros: 10,
        alarma: 5,
        garage: 3
      }
    },
    {
      id: 'seguradora_b_auto_1',
      entidad: 'Seguradora B',
      producto: 'Seguro Auto B치sico',
      tasa: 0,
      premio_anual: 32000,
      cobertura: 'Terceros Completo',
      franquicia: 0,
      requisitos: {
        antiguedadVehiculo: 15,
        valorMinimo: 200000
      },
      beneficios: ['Responsabilidad civil', 'Robo total'],
      descuentos: {
        sinSiniestros: 15,
        alarma: 3
      }
    }
  ]
};

const planes = planesDisponibles[tipoProducto] || [];

return planes.map(plan => ({ 
  ...plan,
  fechaConsulta: new Date().toISOString(),
  cliente: cliente
}));

// Motor de evaluaci칩n y scoring de planes
const items = $input.all();
const planesEvaluados = [];

for (const item of items) {
  const plan = item.json;
  const cliente = plan.cliente;

  // Aplicar filtros b치sicos (requisitos m칤nimos)
  let cumpleRequisitos = true;
  let razonRechazo = [];

  if (plan.requisitos) {
    if (cliente.scoring < plan.requisitos.scoringMinimo) {
      cumpleRequisitos = false;
      razonRechazo.push(`Scoring insuficiente (${cliente.scoring} < ${plan.requisitos.scoringMinimo})`);
    }

    if (cliente.antiguedadLaboral < plan.requisitos.antiguedadMinima) {
      cumpleRequisitos = false;
      razonRechazo.push(`Antig칲edad laboral insuficiente (${cliente.antiguedadLaboral} < ${plan.requisitos.antiguedadMinima} meses)`);
    }

    if (cliente.ingresoNeto < plan.requisitos.ingresoMinimo) {
      cumpleRequisitos = false;
      razonRechazo.push(`Ingreso insuficiente (${cliente.ingresoNeto} < ${plan.requisitos.ingresoMinimo})`);
    }

    if (cliente.montoSolicitado > plan.montoMaximo) {
      cumpleRequisitos = false;
      razonRechazo.push(`Monto excede l칤mite (${cliente.montoSolicitado} > ${plan.montoMaximo})`);
    }

    if (cliente.montoSolicitado < plan.montoMinimo) {
      cumpleRequisitos = false;
      razonRechazo.push(`Monto por debajo del m칤nimo (${cliente.montoSolicitado} < ${plan.montoMinimo})`);
    }
  }

  if (!cumpleRequisitos) {
    planesEvaluados.push({
      ...plan,
      apto: false,
      score: 0,
      razonRechazo: razonRechazo,
      evaluacion: {
        cumpleRequisitos: false,
        cuotaEstimada: 0,
        costoTotal: 0
      }
    });
    continue;
  }

  // Calcular cuota estimada y costo total
  let cuotaEstimada = 0;
  let costoTotal = 0;

  if (cliente.tipoProducto === 'seguro') {
    cuotaEstimada = plan.premio_anual / 12;
    costoTotal = plan.premio_anual;

    // Aplicar descuentos
    if (plan.descuentos) {
      let descuentoTotal = 0;
      Object.values(plan.descuentos).forEach(desc => descuentoTotal += desc);
      cuotaEstimada = cuotaEstimada * (1 - descuentoTotal / 100);
      costoTotal = costoTotal * (1 - descuentoTotal / 100);
    }
  } else {
    // Cr칠ditos (auto, inmueble)
    const plazoMeses = Math.min(cliente.plazoMaximo, plan.plazoMaximo);
    const tasaMensual = plan.tasa / 100 / 12;
    const monto = cliente.montoSolicitado;

    // F칩rmula de cuota francesa
    cuotaEstimada = monto * (tasaMensual * Math.pow(1 + tasaMensual, plazoMeses)) / 
                   (Math.pow(1 + tasaMensual, plazoMeses) - 1);

    costoTotal = cuotaEstimada * plazoMeses;

    // Agregar comisiones
    if (plan.comisiones) {
      costoTotal += monto * (plan.comisiones.apertura / 100);
      costoTotal += plan.comisiones.mantenimiento * plazoMeses;
    }
  }

  // Sistema de scoring (0-100)
  let score = 0;

  // Factor 1: Cuota vs capacidad de pago (30 puntos)
  const ratioCapacidad = cuotaEstimada / cliente.cuotaMaxima;
  if (ratioCapacidad <= 0.7) score += 30;
  else if (ratioCapacidad <= 0.85) score += 20;
  else if (ratioCapacidad <= 1.0) score += 10;

  // Factor 2: Tasa competitiva (25 puntos)
  if (cliente.tipoProducto !== 'seguro') {
    if (plan.tasa <= cliente.tasaMaxima * 0.8) score += 25;
    else if (plan.tasa <= cliente.tasaMaxima * 0.9) score += 20;
    else if (plan.tasa <= cliente.tasaMaxima) score += 10;
  } else {
    // Para seguros, evaluar precio competitivo
    if (cuotaEstimada <= cliente.cuotaMaxima * 0.8) score += 25;
    else if (cuotaEstimada <= cliente.cuotaMaxima * 0.9) score += 20;
    else if (cuotaEstimada <= cliente.cuotaMaxima) score += 10;
  }

  // Factor 3: Beneficios (20 puntos)
  score += Math.min(plan.beneficios.length * 4, 20);

  // Factor 4: Reputaci칩n de la entidad (15 puntos)
  const entidadesPreferidas = ['Banco A', 'Banco B', 'Seguradora A'];
  if (entidadesPreferidas.includes(plan.entidad)) score += 15;
  else score += 8;

  // Factor 5: Condiciones flexibles (10 puntos)
  if (cliente.tipoProducto !== 'seguro') {
    if (plan.plazoMaximo >= 60) score += 5;
    if (plan.comisiones && plan.comisiones.mantenimiento === 0) score += 5;
  } else {
    if (plan.franquicia === 0) score += 5;
    if (plan.descuentos && Object.keys(plan.descuentos).length > 2) score += 5;
  }

  planesEvaluados.push({
    ...plan,
    apto: true,
    score: Math.round(score),
    razonRechazo: [],
    evaluacion: {
      cumpleRequisitos: true,
      cuotaEstimada: Math.round(cuotaEstimada),
      costoTotal: Math.round(costoTotal),
      plazoRecomendado: cliente.tipoProducto !== 'seguro' ? 
        Math.min(cliente.plazoMaximo, plan.plazoMaximo) : 12,
      ratioCapacidad: Math.round(ratioCapacidad * 100) / 100
    }
  });
}

// Ordenar por score descendente
planesEvaluados.sort((a, b) => b.score - a.score);

return planesEvaluados;

// Generar respuesta estructurada con recomendaciones
const planes = $input.all().map(item => item.json);
const cliente = planes[0]?.cliente;

if (!cliente) {
  return [{ json: { error: 'No se encontraron datos del cliente' } }];
}

// Filtrar planes aptos
const planesAptos = planes.filter(p => p.apto);
const planesRechazados = planes.filter(p => !p.apto);

// Preparar respuesta
const respuesta = {
  consulta: {
    timestamp: new Date().toISOString(),
    cliente: {
      tipoProducto: cliente.tipoProducto,
      montoSolicitado: cliente.montoSolicitado,
      ingresoNeto: cliente.ingresoNeto,
      scoring: cliente.scoring
    }
  },

  resumen: {
    totalPlanesEvaluados: planes.length,
    planesAptos: planesAptos.length,
    planesRechazados: planesRechazados.length,
    mejorOpcion: planesAptos.length > 0 ? planesAptos[0].id : null
  },

  recomendaciones: planesAptos.slice(0, 3).map((plan, index) => ({
    ranking: index + 1,
    id: plan.id,
    entidad: plan.entidad,
    producto: plan.producto,
    score: plan.score,

    condiciones: cliente.tipoProducto === 'seguro' ? {
      premioAnual: plan.premio_anual,
      cuotaMensual: plan.evaluacion.cuotaEstimada,
      cobertura: plan.cobertura,
      franquicia: plan.franquicia
    } : {
      tasa: plan.tasa + '%',
      cuotaEstimada: plan.evaluacion.cuotaEstimada,
      plazoRecomendado: plan.evaluacion.plazoRecomendado + ' meses',
      costoTotal: plan.evaluacion.costoTotal
    },

    beneficios: plan.beneficios,

    evaluacion: {
      capacidadPago: plan.evaluacion.ratioCapacidad <= 1 ? 'Adecuada' : 'Ajustada',
      competitividad: plan.score >= 70 ? 'Muy buena' : plan.score >= 50 ? 'Buena' : 'Regular',
      recomendacion: index === 0 ? 'RECOMENDADO' : 'Alternativa ' + (index + 1)
    }
  })),

  alternativasDescartadas: planesRechazados.map(plan => ({
    entidad: plan.entidad,
    producto: plan.producto,
    motivosRechazo: plan.razonRechazo
  })),

  proximosPasos: {
    accionRecomendada: planesAptos.length > 0 ? 
      'Contactar con ' + planesAptos[0].entidad + ' para iniciar proceso' :
      'Mejorar perfil crediticio o ajustar par치metros de b칰squeda',
    documentosNecesarios: [
      'DNI y CUIL',
      '칔ltimos 3 recibos de sueldo',
      'Resumen de cuenta bancaria (3 meses)',
      'Informe de Veraz'
    ],
    tiempoEstimadoAprobacion: '5-10 d칤as h치biles'
  }
};

return [{ json: respuesta }];

